import React, { Component } from "react";
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        // Setting up state
        this.state = {
            userInput: "",
            list: [],
        };
    }

    updateInput(value) {
        this.setState({
            userInput: value,
        });
    }

    addItem() {
        if (this.state.userInput !== "") {
            const userInput = {
                // Add a random id which is used to delete
                id: Math.random(),
                // Add a user value to list
                value: this.state.userInput,
            };
            // Update list
            const list = [...this.state.list];
            list.push(userInput);
            // Reset state
            this.setState({
                list,
                userInput: "",
            });
        }
    }

    deleteItem(key) {
        const list = [...this.state.list];
        // Filter values and leave value which we need to delete
        const updateList = list.filter((item) => item.id !== key);
        // Update list in state
        this.setState({
            list: updateList,
        });
    }

    editItem = (index) => {
        const todos = [...this.state.list];
        const editedTodo = prompt('Edit the todo:');
        if (editedTodo !== null && editedTodo.trim() !== '') {
            let updatedTodos = [...todos];
            updatedTodos[index].value = editedTodo;
            this.setState({
                list: updatedTodos,
            });
        }
    };

    render() {
        return (
            <div className="container shadow-lg p-3 mb-5 bg-body-tertiary rounded mt-5 w-25">
                <div className="head">
                    <h1>TODO LIST</h1>
                </div>

                <hr />

                <div className="row">
                    <div className="col-md">
                        <div class="input-group mb-3 p-4">
                            <input className="form-control p-2" placeholder="add item . . . " value={this.state.userInput} onChange={(item) => this.updateInput(item.target.value)} />
                           <button className="btn btn-info w-25 ms-4" onClick={() => this.addItem()}><b className="text-white">ADD</b></button>
                        </div>
                    </div>
                </div>

                <div className="ms-4 ml-4">
                    {this.state.list.map((item, index) => (
                        <div key={index} className="border p-2 ">
                            <div className="item_value">
                                <h6>{item.value}</h6>
                                <span>
                                    <button class="btn btn-danger" style={{ marginRight: "10px" }} onClick={() => this.deleteItem(item.id)}> Delete </button>
                                    <button class="btn btn-success" onClick={() => this.editItem(index)}>Edit</button>
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    }
}

export default App;
